mapper.delete:

delete from s_b_call_detail
where main_id = #{mainId,jdbcType=INTEGER}
  and call_order = #{callOrder,jdbcType=INTEGER}

mapper.select:

select main_id, call_order, biz_id, user_id, phone_num, call_time, call_result, dayu_result, 
result_time, create_time, modify_time, var_bak1, var_bak2, time_bak1, time_bak2
from s_b_call_detail
where main_id = #{mainId,jdbcType=INTEGER}
  and call_order = #{callOrder,jdbcType=INTEGER}


select main_id, call_order, biz_id, user_id, phone_num, call_time, call_result, dayu_result, 
result_time, create_time, modify_time, var_bak1, var_bak2, time_bak1, time_bak2
from s_b_call_detail 
where main_id = #{mainId,jdbcType=INTEGER}
and call_result = #{callResult,jdbcType=VARCHAR} 
order by call_order
