mapper.select:

select id, farm_id, house_id, temp_id, call_result, succ_time, create_time, modify_time, 
bak1, bak2
from s_b_call_main 
where farm_id = #{farmId,jdbcType=INTEGER} 
and house_id = #{houseId,jdbcType=INTEGER}
 and temp_id = #{tempId,jdbcType=VARCHAR}
 order by id desc limit 1

mapper.update:

update s_b_call_main
set 
  call_result = #{callResult,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
