mapper.select:
SELECT mc.id , mc.farm_id , mc.house_id , DATE_FORMAT(mc.collect_datetime,'%H:%i:%s') collect_datetime , mc.deal_status , (select code_name from s_d_code g where g.code_type = 'ALARM_CODE' and mc.alarm_code = g.biz_code) alarm_code , mc.date_age , mc.week_age , format(mc.inside_temp1,1) inside_temp1 , format(mc.inside_temp2,1) inside_temp2 , format(mc.inside_temp3,1) inside_temp3 , format(mc.inside_temp4,1) inside_temp4 , mc.inside_temp5 , mc.inside_temp6 , mc.inside_temp7 , mc.inside_temp8 , mc.inside_temp9 , format(mc.inside_avg_temp,1) inside_avg_temp , format(mc.inside_set_temp,1) inside_set_temp , mc.high_alarm_temp , mc.low_alarm_temp , format(mc.outside_temp,1) outside_temp , format(mc.inside_humidity,0) inside_humidity , format((mc.inside_avg_temp-mc.inside_set_temp),1) point_temp_diff , format(mc.target_humidity,0) target_humidity , format(mc.co2,1) co2 , mc.power_status , mc.update_datetime , mc.small_fan , mc.larger_fan , mc.refrigeration , mc.heating , mc.fenestella , mc.Tunnel , mc.water_consumption , format(mc.negative_pressure,1) negative_pressure , mc.larger_fan , mc.small_fan , c.code_name refrigeration_name , d.code_name heating_name , e.code_name fenestella_name , f.farm_name_chs farm_name , h.house_name house_name FROM s_b_monitor_curr mc LEFT JOIN s_d_code c ON mc.refrigeration= c.biz_code and c.code_type = 'KGZT' LEFT JOIN s_d_code d ON mc.heating= d.biz_code and d.code_type = 'KGZT' LEFT JOIN s_d_code e ON mc.fenestella= e.biz_code and e.code_type = 'KGZT' LEFT JOIN s_d_farm f ON mc.farm_id= f.id LEFT JOIN s_d_house h ON mc.house_id= h.id WHERE mc.show_flag=1
AND mc.farm_id = #{farmId}
AND mc.house_id = #{houseId}
ORDER BY mc.collect_datetime DESC
