mapper.select:
SELECT farm_id, house_id, date, right(time,2) as time, batch_no, inside_temp1, inside_temp2, inside_temp3, inside_temp4, inside_avg_temp, inside_set_temp, high_alarm_temp, low_alarm_temp, outside_temp FROM rpt_monitor_daily where 1=1
and farm_id = #{farmId}
and house_id = #{houseId}
and batch_no = #{batchId}
AND (`date` BETWEEN #{beginTime} AND #{endTime} )

SELECT farm_id, house_id, year, month, date, batch_no, inside_temp1, inside_temp2, inside_temp3, inside_temp4, inside_avg_temp, inside_set_temp, high_alarm_temp, low_alarm_temp, outside_temp FROM rpt_monitor_month where 1=1
and farm_id = #{farmId}
and house_id = #{houseId}
and batch_no = #{batchId}
AND (`date` BETWEEN #{beginTime} AND #{endTime} )
